import './App.css';
import React, { useState } from 'react';

function App() {
  const [newList, setNewList] = useState('');
  const [lists, setLists] = useState([]);

  const handleSubmit = (e) => {
    e.preventDefault();

    if (newList.length == 0) {
      return;
    }

    const listItem = {
      text: newList,
      complete: false
    };

    setLists([...lists, listItem]);
    setNewList('');
  };

  const handleDelete = (deletId) => {
    const adjustedList = lists.filter((_list, i) => {
      return i !== deletId;
    });
    setLists(adjustedList);
  }

  const checkComplete = (checkId) => {
    const completedTodo = lists.map((list, i) => {
      if (checkId === i) {
        const completedTodo = { ...list, complete: !list.complete };
        return completedTodo;
      }
    });
    setLists(completedTodo);
  }

  return (
    <div className="App">
      <form onSubmit={ handleSubmit }>
        <div class="mb-3">
          <input onChange={(e) => {
            setNewList(e.target.value);
          }}
          value={ newList }
          type="text" class="form-control"/>
        </div>
        <button type="submit" class="btn btn-primary">Add</button>
      </form>

      {lists.map((list, i) => {
        const crossOut = [];

        if (list.complete) {
          crossOut.push("crossed-out");
        }

        return (
          <div key={i} class="todo-list">
            <p className={crossOut.join(" ")} >{ list.text }</p>
            <input onChange={(e) => {
              checkComplete(i);
            }}
            checked= { list.complete }
            type="checkbox" />
            <button onClick={(e) => {
              handleDelete(i);
            }}>Delete</button>
          </div> 
        );
      })}
    </div>
  );
}

export default App;
